module.exports = {
  range: function (n) {
    var result = [];
    for (var i = 0; i < n; i++) {
      result.push(i);
    }
    return result;
  },
  isEven: function (n) {
    return n % 2 === 0;
  },
  add: function (a, b) {
    if (typeof a !== 'number' || typeof b !== 'number') {
      throw TypeError('Expected numbers as arguments');
    }
    return a + b;
  }
};
